con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

// How to get Variables from the test case
log.info context.expand('${#TestCase#name}')
log.info context.expand('${#TestCase#age}')

//How to get properties or set values to properties from other TC different than this one
log.info testRunner.testCase.testSuite.testCases["GetEmployee"].getPropertyValue("id")
testRunner.testCase.testSuite.testCases["GetEmployee"].setPropertyValue("id","1998")
log.info testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")

//How to get properties from this same Test Case
log.info testRunner.testCase.getPropertyValue("dept")

log.info testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"].getPropertyValue("Request")
log.info testRunner.testCase.testSuite.project.getPropertyValue("gender")

// ***AddEmployee***
// Xml Holder to add data to request in AddEmployee TC
def addReq = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["Add"].getPropertyValue("Request")
def xmlAdd = new XmlHolder(addReq)

//Getting properties from AddEmployee TC
def name = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("name")
def age = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")
def id = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("id")

// Setting values for request of AddEmployee TC
xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department","Gpe")
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)

// Getting current Request with values previously defined and replacing value of request property and changing to teh current one
def newAddXml = xmlAdd.getXml()
testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["Add"].setPropertyValue("Request", newAddXml)

// Running AddEmployee Request
def addTestStep = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["Add"]
def contextAddEmployee = new WsdlTestRunContext(addTestStep);
addTestStep.run(testRunner, contextAddEmployee)

// ***GetEmployee***
// Xml Holder to add data to request in GetEmployee TC
def getReq = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"].getPropertyValue("Request")
def getEmpXml = new XmlHolder(getReq)

// Setting values for request of GetEmployee TC
getEmpXml.setNodeValue("//typ:getEmployeeDetails/typ:employeeName", name)

// Getting current Request with values previously defined and replacing value of request property and changing to teh current one
def newgetEmpXml = getEmpXml.getXml()
testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"].setPropertyValue("Request", newgetEmpXml)

// Running GetEmployee Request
def getTestStep = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"]
def contextGetEmplyee = new WsdlTestRunContext(getTestStep)
getTestStep.run(testRunner, contextGetEmplyee)

// Validation of response Get and variable name
def getRes = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"].getPropertyValue("Response")
def getEmpRes = new XmlHolder(getRes)
def getResponse = getEmpRes.getNodeValue("//ns:name")
log.info getResponse
log.info name

assert getResponse == name

// ***DeleteEmployee***
// Xml Holder to add data to request in DeleteEmployee TC
def delReq = testRunner.testCase.testSuite.testCases["DeleteEmployee"].testSteps["Delete"].getPropertyValue("Request")
def delEmpXml = new XmlHolder(delReq)

// Setting values for request of DeleteEmployee TC
delEmpXml.setNodeValue("//typ:deleteEmployee/typ:employeeName", name)

// Getting current Request with values previously defined and replacing value of request property and changing to teh current one
def newdelEmpXml = delEmpXml.getXml()
testRunner.testCase.testSuite.testCases["DeleteEmployee"].testSteps["Delete"].setPropertyValue("Request", newdelEmpXml)

// Running DeleteEmployee Request
def delTestStep = testRunner.testCase.testSuite.testCases["DeleteEmployee"].testSteps["Delete"]
def contextDelEmplyee = new WsdlTestRunContext(delTestStep)
delTestStep.run(testRunner, contextDelEmplyee)

//***Validate that employee was deleted***
// Setting value for request in GetEmployee TC
getEmpXml.setNodeValue("//typ:getEmployeeDetails/typ:employeeName", name)

// Setting the values of the current request in the new request
newgetEmpXml = getEmpXml.getXml()
testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"].setPropertyValue("Request", newgetEmpXml)

// Running GetRequest
getTestStep = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"]
contextGetEmplyee = new WsdlTestRunContext(getTestStep)
getTestStep.run(testRunner, contextGetEmplyee)

// Getting Response
getRes = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["Get"].getPropertyValue("Response")
getEmpRes = new XmlHolder(getRes)
def getResponseValidation = getEmpRes.getNodeValue("//ax21:message")
log.info getResponseValidation
assert getResponseValidation == "Employee does not exist"
